named-native-query.query:
			 select aud_id, ins_id, ins_nombre, aud_codigo, aud_nombre, 
			 if(isnull(timestampdiff(day, aud_fecha_programado, aud_fecha_planificacion)) = 1 , 0 , timestampdiff(day, aud_fecha_programado, aud_fecha_asignada)) tiempo_programado,
 			 if(isnull(timestampdiff(day, aud_fecha_programado, aud_fecha_planificacion)) = 1 , 0 , timestampdiff(day, aud_fecha_asignada, aud_fecha_planificacion)) tiempo_asignado,
			 if(isnull(timestampdiff(day, aud_fecha_planificacion, aud_fecha_ejecucion)) = 1 , 0 , timestampdiff(day, aud_fecha_planificacion, aud_fecha_ejecucion)) tiempo_planificacion,
			 if(isnull(timestampdiff(day, aud_fecha_ejecucion, aud_fecha_informe)) = 1 , 0 , timestampdiff(day, aud_fecha_ejecucion, aud_fecha_informe)) tiempo_ejecucion,
			 if(isnull(timestampdiff(day, aud_fecha_informe, aud_fecha_seguimiento)) = 1 , 0 , timestampdiff(day, aud_fecha_informe, aud_fecha_seguimiento)) tiempo_informe,
			 if(isnull(timestampdiff(day, aud_fecha_seguimiento, aud_fecha_finalizada)) = 1 , 0 , timestampdiff(day, aud_fecha_seguimiento, aud_fecha_finalizada)) tiempo_seguimiento
			 from auditoria a 
			 inner join plan_anual b on a.aud_pla_id = b.pla_id
			 inner join institucion c on b.pla_ins_id = ins_id
			 where ins_id = ?1
