named-native-query.query:
select "TOTAL",
if(isnull(sum(case when fase = "PROGRAMADA" then sub else 0 end)) = 1, 0, sum(case when fase = "PROGRAMADA" then sub else 0 end) ) as "PROGRAMADA",
if(isnull(sum(case when fase = "ASIGNADA" then sub else 0 end)) = 1, 0, sum(case when fase = "ASIGNADA" then sub else 0 end) ) as "ASIGNADA",
if(isnull(sum(case when fase = "PLANIFICACION" then sub else 0 end)) = 1, 0, sum(case when fase = "PLANIFICACION" then sub else 0 end))  as "PLANIFICACION",
if(isnull(sum(case when fase = "EJECUCION" then sub else 0 end)) = 1, 0, sum(case when fase = "EJECUCION" then sub else 0 end)) as "EJECUCION",
if(isnull(sum(case when fase = "INFORME" then sub else 0 end)) = 1, 0, sum(case when fase = "INFORME" then sub else 0 end)) as  "INFORME",
if(isnull(sum(case when fase = "SEGUIMIENTO" then sub else 0 end)) = 1, 0, sum(case when fase = "SEGUIMIENTO" then sub else 0 end)) as "SEGUIMIENTO",
if(isnull(sum(case when fase = "FINALIZADA" then sub else 0 end)) = 1, 0, sum(case when fase = "FINALIZADA" then sub else 0 end)) as "FINALIZADA"
from (
select "TOTAL", case when aud_fase = 0 then "PROGRAMADA"
when aud_fase = 1 then "ASIGNADA"
when aud_fase = 2 then "PLANIFICACION"
when aud_fase = 3 then "EJECUCION" 
when aud_fase = 4 then "INFORME"
when aud_fase = 5 then "SEGUIMIENTO"
when aud_fase = 6 then "FINALIZADA"
end as fase, count(aud_id) sub
from auditoria  a 
inner join plan_anual b on a.aud_pla_id = b.pla_id
where a.reg_activo = 1 and b.pla_ins_id = ?1 and aud_fecha_inicio_programado between ?2 and ?3
group by aud_fase ) as r

			 select aud_id, ins_id, ins_nombre, aud_codigo, aud_nombre, 
			 if(isnull(timestampdiff(day, aud_fecha_programado, aud_fecha_planificacion)) = 1 , 0 , timestampdiff(day, aud_fecha_programado, aud_fecha_asignada)) tiempo_programado,
 			 if(isnull(timestampdiff(day, aud_fecha_programado, aud_fecha_planificacion)) = 1 , 0 , timestampdiff(day, aud_fecha_asignada, aud_fecha_planificacion)) tiempo_asignado,
			 if(isnull(timestampdiff(day, aud_fecha_planificacion, aud_fecha_ejecucion)) = 1 , 0 , timestampdiff(day, aud_fecha_planificacion, aud_fecha_ejecucion)) tiempo_planificacion,
			 if(isnull(timestampdiff(day, aud_fecha_ejecucion, aud_fecha_informe)) = 1 , 0 , timestampdiff(day, aud_fecha_ejecucion, aud_fecha_informe)) tiempo_ejecucion,
			 if(isnull(timestampdiff(day, aud_fecha_informe, aud_fecha_seguimiento)) = 1 , 0 , timestampdiff(day, aud_fecha_informe, aud_fecha_seguimiento)) tiempo_informe,
			 if(isnull(timestampdiff(day, aud_fecha_seguimiento, aud_fecha_finalizada)) = 1 , 0 , timestampdiff(day, aud_fecha_seguimiento, aud_fecha_finalizada)) tiempo_seguimiento
			 from auditoria a 
			 inner join plan_anual b on a.aud_pla_id = b.pla_id
			 inner join institucion c on b.pla_ins_id = ins_id
			 where ins_id = ?1 and a.reg_activo = 1 and aud_fecha_inicio_programado between ?2 and ?3
